/**
 * Retrieve application options (for caching)
 *
 * @return array
 */
public function getOptions()
{
    return $this->_options;
}

/**
 * Is an option present?
 *
 * @param  string $key
 * @return bool
 */
public function hasOption($key)
{
    return in_array(strtolower($key), $this->_optionKeys);
}

